import React from "react";
import { Tabs } from "antd";
import styled from "styled-components";
import CategoryCard from "./CategoryCard";
import { useTranslation } from "react-i18next";

const { TabPane } = Tabs;

const CategoryGrid = styled.div`
  display: flex;
  gap: 10px;
  flex-wrap: nowrap;
  overflow-x: auto;
  padding-bottom: 10px;
`;

// Custom styled Tabs
const StyledTabs = styled(Tabs)`
  .ant-tabs-nav {
    color: white;
  }

  .ant-tabs-tab {
    color: white !important;
    font-weight: bold;
  }

  /* Ensures selected tab text remains white */
  .ant-tabs-tab.ant-tabs-tab-active,
  .ant-tabs-tab.ant-tabs-tab-active .ant-tabs-tab-btn {
    color: white !important;
  }

  /* Prevents blue color when clicking a tab */
  .ant-tabs-tab .ant-tabs-tab-btn:focus,
  .ant-tabs-tab .ant-tabs-tab-btn:hover {
    color: white !important;
  }

  /* Changes the active tab's underline to green */
  .ant-tabs-ink-bar {
    background-color: #27ae60 !important;
  }
`;


export const CategoryTabs: React.FC = () => {
  
  const { t } = useTranslation();
  
const categories = [
  { title: t('searchBar.music') , image: "data:image/jpeg;base64," },
  { title: t('searchBar.theaterArts'), image: "data:image/jpeg;base64," },
  { title: t('searchBar.sports'), image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ5Yl7YjMUGAyNBMeFhE88NFRWF7mpLumHzBg&s" },
  { title: t('searchBar.others'), image: "data:image/jpeg;base64," },
];

const locations = [
  { title: t('searchBar.hcm'), image: "https://thanhnien.mediacdn.vn/Uploaded/maiha/2022_06_10/phong-canh-tp-hcm-anh-doc-lap-47-1353.jpg" },
  { title: t('searchBar.hanoi'), image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQQcZHDNMVQ70Y7nA9baMizg1Db2gTkQDnGJA&s" },
  { title: t('searchBar.dalat'), image: "data:image/jpeg;base64," },
  { title: t('searchBar.otherLocations'), image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPPkl2kgxwoEsSiqrDl04zlwqFEm9fWgMMyg&s" },
];
  return (
    <StyledTabs defaultActiveKey="1">
      <TabPane tab={t('searchBar.browseByCategory')} key="1">
        <CategoryGrid>
          {categories.map((item) => (
            <CategoryCard key={item.title} title={item.title} image={item.image} />
          ))}
        </CategoryGrid>
      </TabPane>
      <TabPane tab={t('searchBar.browseByLocation')}  key="2">
        <CategoryGrid>
          {locations.map((item) => (
            <CategoryCard key={item.title} title={item.title} image={item.image} />
          ))}
        </CategoryGrid>
      </TabPane>
    </StyledTabs>
  )
};